(function() {
  var creator, store;

  store = null;

  creator = function(Vue) {
    var Store;
    if (store == null) {
      Store = Vue.extend(creator.obj);
      store = new Store();
    }
    return store;
  };

  creator.obj = require('./gradient-store-component');

  module.exports = creator;

}).call(this);
