(function() {
  var Velocity, c, clone, comp;

  c = require("vue-collapsible/collapsible");

  Velocity = require("velocity-animate");

  clone = require("lodash/clone");

  comp = {
    template: c.template,
    mixins: c.mixins,
    props: clone(c.props),
    methods: c.methods
  };

  comp.props.scrollTransition["default"] = function(top) {
    var body, docEl, scrollTop;
    body = document.body;
    docEl = document.documentElement;
    scrollTop = window.pageYOffset || docEl.scrollTop || body.scrollTop;
    scrollTop += top;
    return Velocity(docEl, "scroll", {
      duration: 100,
      offset: scrollTop
    });
  };

  module.exports = comp;

}).call(this);
