(function() {
  var creator;

  creator = require('./toaster');

  module.exports = {
    computed: require("vue-mixins/vue").computed,
    compiled: function() {
      var toaster;
      toaster = creator(this.Vue);
      if (toaster.used === 0) {
        document.body.appendChild(toaster.$el);
      }
      toaster.used++;
      return this.toast = toaster.toast;
    },
    beforeDestroy: function() {
      var toaster;
      toaster = creator(this.Vue);
      toaster.used--;
      if (toaster.used === 0) {
        toaster.clear();
        return document.body.removeChild(toaster.$el);
      }
    }
  };

}).call(this);
